/* Gerando dados para a tabela CLASFISC */
EXECUTE BLOCK
AS
DECLARE V_CODEMPCLASFISC TYPE OF COLUMN CLASFISC.CODEMPCLASFISC;
DECLARE V_CODCLASFISC TYPE OF COLUMN CLASFISC.CODCLASFISC;
DECLARE V_DESCRICAO TYPE OF COLUMN CLASFISC.DESCRICAO;
BEGIN
  FOR SELECT
    NCM.CODIGO,
    UPPER(SUBSTRING(NCM.DESCRICAO FROM 1 FOR 40))
  FROM
    NCM
  WHERE
    CHAR_LENGTH(TRIM(NCM.CODIGO)) = 8
  ORDER BY
    NCM.CODIGO
  INTO
    :V_CODCLASFISC,
    :V_DESCRICAO
  DO BEGIN
    FOR SELECT
      R_CODEMPRESA
    FROM
      BUSCALISTAEMPRESA('CLASFISC')
    INTO
      :V_CODEMPCLASFISC
    DO BEGIN
      IF (NOT EXISTS(
        SELECT
          1
        FROM
          CLASFISC
        WHERE
          CLASFISC.CODEMPCLASFISC = :V_CODEMPCLASFISC AND
          CLASFISC.CODCLASFISC    = :V_CODCLASFISC
      )) THEN
          CODEMPCLASFISC,
          CODCLASFISC,
          CLASFISCAL,
          DESCRICAO,
          ATIVO)
        VALUES (
          :V_CODEMPCLASFISC,
          :V_CODCLASFISC,
          :V_CODCLASFISC,
          :V_DESCRICAO,
          'S');
    END
  END
END!
COMMIT!